inputLines = puzzleInput.splitlines()

lineNumber = 0
numberOfMatches = 0

# scan for all matches of XMAS within the crossword, including if backwards
for line in inputLines:
    
    for charIndex in range(0, len(line)):

        # at the current position, do the following if each approach has enough room

        # 1 scan from left to right
        if charIndex <= (len(line) - 4):
            attemptedMatch = line[charIndex] + line[charIndex + 1] + line[charIndex + 2] + line[charIndex + 3]
            if attemptedMatch == "XMAS": numberOfMatches += 1

        # 2 scan from right to left
        if charIndex == 3 or charIndex == 139:
            print('hi')
        if charIndex >= 3:
            attemptedMatch = line[charIndex] + line[charIndex - 1] + line[charIndex -2] + line[charIndex - 3]
            if attemptedMatch == "XMAS": numberOfMatches += 1

        # 3 scan upwards 
        if lineNumber >= 3:
            attemptedMatch = line[charIndex] + inputLines[lineNumber - 1][charIndex] + inputLines[lineNumber - 2][charIndex] + inputLines[lineNumber - 3][charIndex]
            if attemptedMatch == "XMAS": numberOfMatches += 1

        # 4 scan downwards
        if lineNumber >= 136:
            print('')
        if lineNumber <= (len(inputLines) - 4):
            attemptedMatch = line[charIndex] + inputLines[lineNumber + 1][charIndex] + inputLines[lineNumber + 2][charIndex] + inputLines[lineNumber + 3][charIndex]
            if attemptedMatch == "XMAS": numberOfMatches += 1

        # 5 scan diagonal NE
        if lineNumber >= 3 and charIndex <= (len(line) - 4):
            attemptedMatch = line[charIndex] + inputLines[lineNumber - 1][charIndex + 1] + inputLines[lineNumber - 2][charIndex + 2] + inputLines[lineNumber - 3][charIndex + 3]
            if attemptedMatch == "XMAS": numberOfMatches += 1

        # 6 scan diagonal NW
        if lineNumber >= 3 and charIndex >= 3:
            attemptedMatch = line[charIndex] + inputLines[lineNumber - 1][charIndex - 1] + inputLines[lineNumber - 2][charIndex - 2] + inputLines[lineNumber - 3][charIndex - 3]
            if attemptedMatch == "XMAS": numberOfMatches += 1

        # 7 scan diagonal SE
        if lineNumber <= (len(inputLines) - 4) and charIndex <= (len(line) - 4):
            attemptedMatch = line[charIndex] + inputLines[lineNumber + 1][charIndex + 1] + inputLines[lineNumber + 2][charIndex + 2] + inputLines[lineNumber + 3][charIndex + 3]
            if attemptedMatch == "XMAS": numberOfMatches += 1

        # 8 scan diagonal SW
        if lineNumber <= (len(inputLines) - 4) and charIndex >= 3:
            attemptedMatch = line[charIndex] + inputLines[lineNumber + 1][charIndex - 1] + inputLines[lineNumber + 2][charIndex - 2] + inputLines[lineNumber + 3][charIndex - 3]
            if attemptedMatch == "XMAS": numberOfMatches += 1


    lineNumber += 1

print(numberOfMatches)

