inputLines = puzzleInput.splitlines()

lineNumber = 0
numberOfMatches = 0

# scan for all matches of XMAS within the crossword, including if backwards
for line in inputLines:
    
    for charIndex in range(0, len(line)):
        if (lineNumber > 0 and lineNumber <= len(inputLines) - 1) and (charIndex > 0 and charIndex <= len(line) - 2) and line[charIndex] == "A":
            # NW to SE match
            NWToSE = inputLines[lineNumber - 1][charIndex - 1] + line[charIndex] + inputLines[lineNumber + 1][charIndex + 1]
            # NE to SW match
            NEToSW = inputLines[lineNumber - 1][charIndex + 1] + line[charIndex] + inputLines[lineNumber + 1][charIndex - 1]

            NWToSEMatch = NWToSE == "MAS" or NWToSE == "SAM"
            NEToSWMatch = NEToSW == "MAS" or NEToSW == "SAM"
            if NWToSEMatch and NEToSWMatch: numberOfMatches += 1

    lineNumber += 1

print(numberOfMatches)

